           DELETE PIZZA
        ======================= */
        $(&#039;.btn-delete&#039;).on(&#039;click&#039;, function() {
            if (!confirm(&#039;¿Eliminar esta pizza?&#039;)) return;

            let btn = $(this);
            let id = btn.data(&#039;id&#039;);

            $.ajax({
                type: &quot;POST&quot;,
                url: &quot;http://pizzeria.la.cubana/admin/deletePizza&quot;,
                data: {
                    id,
                    imgURL: btn.data(&#039;img&#039;)
                },
                dataType: &quot;json&quot;,
                success: function(response) {
                    if (response.error === 0) {
                        $(&#039;#pizza-&#039; + id).fadeOut(300, function() {
