            if (!confirm(&#039;¿Eliminar este producto?&#039;)) return;

            let btn = $(this);
            let id = btn.data(&#039;id&#039;);

            btn.text(&#039;Eliminando&#039;).prop(&#039;disabled&#039;, true);

            $.ajax({
